sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var mailReader = new GlideAjax('MailReader');
    mailReader.addParam('sysparm_name', 'getMail');
    var requestorId = g_form.getValue('u_requested_for');
    jslog("Jjj----1---FE " + requestorId);
    mailReader.addParam('sysparm_vikiUserId', requestorId);

    mailReader.getXMLAnswer(mailReaderCallback);

}

function mailReaderCallback(mail) {
    jslog("Jjj----3---FE " + mail);
    g_form.setValue('u_mail_req', mail);
}
