var MailReader = Class.create();
MailReader.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getMail: function() {
        try {
            gs.info("-----TT-------1 getMail gs.isInteractive())=" + gs.isInteractive());
            var userId = this.getParameter('sysparm_vikiUserId');
            gs.info("-----TT-------2 userId = " + userId);
            var gr = new GlideRecord('sys_user');

            var user = gr.get(userId);
            gs.info("-----TT-------3 user = " + user);
            gs.info("-----TT-------4  user.email  = " + gr.email);

			var u = new MathUtils();
            return gr.email + '_' + u.inc(7);
        } catch (err) {
			gs.error(err);
        }
    },

    type: 'MailReader'
});

