var InputValidator = Class.create();
InputValidator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    isValidEmail: function() {
        gs.warn("VV--1--SERVER----- in isValidEmail");
        var mail = this.getParameter('sysparm_mail');

        gs.warn("VV--2--SERVER----- in mail [" + mail + "]");
        var res = mail.contains('@');

        gs.warn("VV--3---SERVER---- in res [" + res + "]");
        return res;
    },

    type: 'InputValidator'

});

