sys_ui_policy.script_false:
function onCondition() {
	// Clear the field message written by the Execute if true script for the Other field
	//g_form.hideFieldMsg('u_other',true);
}

sys_ui_policy.script_true:
function onCondition() {
    jslog("VV.onCondition 1-------");
    try {
        var validatorAjax = new GlideAjax('InputValidator');
        jslog("VV.onCondition--2---validatorAjax-- [" + validatorAjax + "]");
        validatorAjax.addParam('sysparm_name', 'isValidEmail');
        validatorAjax.addParam('sysparm_mail', g_form.getValue('description'));
        validatorAjax.getXMLAnswer(doSth);

    } catch (err) {
        jslog("VV.onCondition-4----INCORRECT VALIDATOR Class [" + err + "]");
    }

    function doSth(isValidEmail) {
        //var isValidEmail = response.responseXML.documentElement.getAttribute("answer");


        jslog("VV2.doSth ----3 [" + isValidEmail + "]");
        jslog("VV2.doSth ----3.2 [" + !isValidEmail + "]");
        if (isValidEmail == 'false') {
            jslog("VV2.doSth ----5 ");
            // g_form.addErrorMessage('ALOoooooooo');
            // g_form.setValue('description', 'v.tsankova@gmail.com');
            g_form.showErrorBox('description', 'CLient Script had detected INCORRECT Email');
        } else if (isValidEmail == 'true') {
            g_form.showFieldMsg('description', 'CLient Script had detected CORRECT Email', 'info');
        }
    }

}
